elves_data_stream = ""

MARKER_LENGTH = 14


def is_marker(marker_candidat):
    deleted_duplicates = list(set(list(marker_candidat)))
    if len(deleted_duplicates) == MARKER_LENGTH:
        return True
    return False


def get_marker_position(string_datastream):
    marker_pos = -1

    datastream_length = len(string_datastream)
    for end in range(MARKER_LENGTH, datastream_length + 1):
        marker_candidat = string_datastream[end-MARKER_LENGTH:end]
        print(end, marker_candidat)
        if is_marker(marker_candidat):
            marker_pos = end
            break

    return marker_pos


print(f"The marker is at {get_marker_position(elves_data_stream)}")
